import React from 'react'
import PropTypes from 'prop-types'
import { Avatar } from 'antd';

const AppBadge = props => {

    return (
        <div className='app-chip px-4'>
            <div className='d-flex align-center h-100'>
                <Avatar
                    size={10}
                    style={
                        props.type.status === 'Paid' 
                        ? {backgroundColor: '#6DD230'}
                        : props.type.status === 'Pending'
                        ? {backgroundColor: '#00A1ED'}
                        : {backgroundColor: '#FE4D97'}
                    }
                />
                <span className='text-small font-500 ml-3'>{props.type.status}</span>
            </div>
        </div>
    )
}

AppBadge.propTypes = {
    type: PropTypes.object.isRequired
}

export default AppBadge