
import React from 'react'
import {Link, NavLink, useRouteMatch} from 'react-router-dom'
import {Typography} from 'antd'
const { Text } = Typography;
import routes from 'routes'
import Icons from './icons'

import Logo from 'assets/img/logo.png'
import './index.css'

const AppSidebar = () => {
	const {path} = useRouteMatch()

	return (
		<div className='sidebar-inner d-flex f-column h-100'>
			<Link to='/' className='sidebar-header d-flex align-center justify-center'>
				<img src={Logo} alt="App Logo" />
			</Link>

			<ul className='sidebar-content flex-fill'>
				{routes.map((route) => (
					<li key={route.name}>
						<NavLink
							to={route.path}
							className='navigation-items d-flex align-center p-relative'
						>
							<Icons name={route.name.toLocaleLowerCase()} className='d-flex align-center pl-6 pr-5' />
							<span className='font-500'>{route.name}</span>
						</NavLink>
					</li>
				))}
			</ul>

			<ul className='sidebar-bottom'>
				<li>
					<a href='https://www.google.com/' target='_blank' className='d-flex align-center p-relative font-500 pl-8'>
						Help Center
					</a>
				</li>
				<li>
					<NavLink to={`${path}/contact-us`} className='d-flex align-center p-relative font-500 pl-8'>
						Contact Us
					</NavLink>
				</li>
				<li>
					<a className='d-flex align-center p-relative font-500 pl-8 pointer'>
						<Text type="danger" className='d-flex align-center p-relative font-500 pointer'>
							Log Out
						</Text>
					</a>
				</li>
			</ul>
		</div>
	)
}

export default AppSidebar