import React from 'react'
import PropTypes from 'prop-types'
import { Avatar } from 'antd';
import {DollarCircleOutlined, EllipsisOutlined, PoundCircleOutlined } from '@ant-design/icons';


const AccountItems = props => {
    return (
        <div className='card-block d-flex align-center py-3'>
            <div className='card-block__circle mr-6'>
                <Avatar
                    size={40}
                    style={{ backgroundColor: 'rgba(var(--primary), 0.2)' }}
                    className='child-center'
                    icon={
                        props.item.accountCurrency === 'Dollar' 
                        ? <DollarCircleOutlined style={{color: 'rgba(var(--primary), 1)'}} /> 
                        : <PoundCircleOutlined style={{color: 'rgba(var(--primary), 1)'}} />
                    }
                />
            </div>
            <div className='card-block__text flex-fill mr-4'>
                <h5 className='text-dark font-500 mb-0 line-base'>{props.item.accountName}</h5>
                <span className='text-grey text-small font-400'>Account</span>
            </div>
            <div className='card-block__text mr-12'>
                <h5 className='text-dark font-500 mb-0 line-base'>{props.item.accountBalance}</h5>
                <span className='text-grey text-small font-400'>Balance</span>
            </div>
            <div className='card-block__action pointer'>
                <EllipsisOutlined style={{fontSize: '25px', color: 'rgba(var(--grey), 1)'}} />
            </div>
        </div>
    )
}

AccountItems.propTypes = {
    item: PropTypes.object.isRequired
}

export default AccountItems